var FormDropzone=function(){
    return{init:function(){
        Dropzone.options.myDropzone={dictDefaultMessage:"",init:function()
            {
                this.on("addedfile",function(e){
                var n=Dropzone.createElement("<a href='javascript:;'' class='btn red btn-sm btn-block'>Remove</a>"),
                    t=this;n.addEventListener("click",function(n){n.preventDefault(),n.stopPropagation(),t.removeFile(e)})
                    ,e.previewElement.appendChild(n)

            }
                // alert(this.getAcceptedFiles().length);


            )}}
    }}}();
jQuery(document).ready(function(){
    FormDropzone.init()
});

